export {default as CellPage} from './loader/CellPage';

export {default as default} from './router/CellRouter';

export {default as CellRoute} from './router/CellRoute';

import {component} from 'web-cell';

import CellPage from './CellPage';

const loader = new Map();



@component()
/**
 * Page components loader
 */
export default  class CellLoader extends HTMLElement {

    constructor() {  super();  }

    connectedCallback() {

        loader.set(this, {
            base:  new URL(this.getAttribute('base'), this.baseURI) + ''
        });
    }

    disconnectedCallback() {  loader.delete( this );  }

    /**
     * URL base for loading
     *
     * @type {string}
     */
    get base() {  return  loader.get( this ).base;  }

    /**
     * `<cell-page />` children
     *
     * @type {CellPage[]}
     */
    get pageList() {

        return  [... this.children].filter(element => element instanceof CellPage);
    }

    /**
     * All `<cell-page />`s in this page
     *
     * @type {CellPage[]}
     */
    static get pageList() {

        return  [ ].concat(... [... loader.keys()].map(item => item.pageList));
    }

    /**
     * @param {string} tag - Tag name of a Page component
     *
     * @return {Promise}
     */
    static load(tag) {

        return  this.pageList.filter(page => page.name === tag)[0].load();
    }
}

import {component} from 'web-cell';

const ESM = (! document.querySelector(
        'script[src$="custom-elements-es5-adapter.js"]'
    )),
    page_data = new WeakMap();



@component()
export default  class CellPage extends HTMLElement {

    constructor() {  super();  }

    connectedCallback() {

        const loader = this.parentNode;

        if (loader.tagName === 'CELL-LOADER')
            page_data.set(this, {
                path:  new URL(
                    this.getAttribute('path') || '',  loader.base
                ) + ''
            });
        else
            throw new DOMError(
                '<cell-page /> must be a child of <cell-loader />'
            );
    }

    /**
     * URL of this component
     *
     * @type {string}
     */
    get path() {  return  page_data.get( this ).path;  }

    /**
     * Tag name of this component
     *
     * @type {string}
     */
    get name() {  return  this.path.split('/').slice(-1)[0];  }

    /**
     * @type {boolean}
     */
    get loaded() {  return  (!! window.customElements.get( this.name ));  }

    /**
     * @return {Promise}
     */
    load() {

        return  this.loaded ?
            Promise.resolve() :
            new Promise((resolve, reject)  =>  document.head.append(
                Object.assign(document.createElement('script'), {
                    onload:   resolve,
                    onerror:  reject,
                    type:     ESM ? 'module' : 'text/javascript',
                    src:      `${this.path}.js`
                })
            ));
    }
}

import {component} from 'web-cell';

import RouteMap from './RouteMap';

const path_tag = new WeakMap(), route_map = new RouteMap();



@component()
/**
 * Route entry
 */
export default  class CellRoute extends HTMLElement {

    constructor() {  super();  }

    connectedCallback() {

        if (this.parentNode.tagName !== 'CELL-ROUTER')
            throw new DOMError(
                '<cell-route /> must be a child of <cell-router />'
            );

        const path = this.getAttribute('path'), tag = this.getAttribute('tag');

        path_tag.set(this,  {path, tag});

        route_map.set(path, tag);
    }

    /**
     * @type {string}
     */
    get path() {  return  path_tag.get( this ).path;  }

    /**
     * Tag name of a Page component
     *
     * @type {string}
     */
    get tag() {  return  path_tag.get( this ).tag;  }

    /**
     * @protected
     *
     * @type {RouteMap}
     */
    static get map() {  return route_map;  }
}

import {component, Component} from 'web-cell';

import CellRoute from './CellRoute';

import RouteMap from './RouteMap';

import PageStack from './PageStack';

const on = Component.prototype.on, route_map = new RouteMap();

const path_mode = {
    hash:  1,
    path:  1
};

var page;



@component()
/**
 * Routes elements wrapper
 */
export default  class CellRouter extends HTMLElement {

    constructor() {

        super();

        /**
         * @type {string}
         */
        this.rootURL = document.URL.split( /\?|#/ )[0];

        /**
         * @type {boolean}
         */
        this.loading = false;
    }

    /**
     * History path mode (`hash` or `path`)
     *
     * @type {string}
     */
    get mode() {

        const value = this.getAttribute('mode');

        return  (value in path_mode)  ?  value  :  'hash';
    }

    set mode(value) {

        if (value in path_mode)
            this.setAttribute('mode',  page.mode = value + '');
    }

    /**
     * Current route path
     *
     * @type {string}
     */
    get path() {

        return  (this.mode === 'hash')  ?
            window.location.hash.slice( 1 )  :
            document.URL.slice( this.rootURL.length );
    }

    /**
     * @private
     */
    async boot() {

        page = new PageStack('main', this.mode);

        on.call(page.container,  'pagechanged',  event => {

            const data = event.detail;

            route_map.trigger(data.to.path, data.to, data.from);
        });

        if ((this.mode !== 'hash')  ||  !this.path)  return;

        const path = this.path;

        history.replaceState({ }, document.title, window.location.pathname);

        await this.navTo( path );
    }

    connectedCallback() {

        on.call(document,  'DOMContentLoaded',  this.boot.bind( this ));

        const router = this;

        on.call(document.body,  'click',  'a[href]',  function (event) {

            if (!router.loading  &&  ((this.target || '_self')  ===  '_self')) {

                event.preventDefault();   router.navTo( this );
            }
        });
    }

    /**
     * Key for path, Value for tag
     *
     * @type {object}
     */
    get map() {

        const route = { };

        for (let child of this.children)
            if (child instanceof CellRoute)  route[ child.path ] = child.tag;

        return route;
    }

    /**
     * @protected
     *
     * @type {PageStack}
     */
    get stack() {  return page;  }

    /**
     * @param {String|URL|Element} link - An URL or Element with `href` attribute
     */
    async navTo(link) {

        this.loading = true;

        if (!(link instanceof HTMLElement))
            link = Object.assign(document.createElement('a'), {
                href:  (new URL(link, window.location) + '')
                    .replace(this.rootURL, '')
            });

        const path = link.getAttribute('href');

        const tag = CellRoute.map.trigger( path );

        if ( tag )
            await page.push(tag,  path,  link.title || link.textContent.trim());

        this.loading = false;
    }

    /**
     * Register route handler
     *
     * @param {string|RegExp} path    - **Plain path**, **Path with colon parameters** or
     *                                  **Regular expression**
     * @param {RouteHandler}  handler
     *
     * @return {Function} This class
     */
    static route(path, handler) {

        route_map.set(path, handler);

        return this;
    }
}

import CellLoader from '../loader/CellLoader';


/**
 * Page DOM storage
 */
export default  class PageStack {
    /**
     * @param {string} container     - CSS selector of Page container
     * @param {string} [mode='hash'] - History path mode (`hash` or `path`)
     */
    constructor(container, mode) {
        /**
         * Page count
         *
         * @type {number}
         */
        this.length = 1;

        /**
         * Index of last page
         *
         * @type {number}
         */
        this.last = 0;

        /**
         * State of last page
         *
         * @type {HistoryState}
         */
        this.lastState = {
            tag: '',  path: '',  title: document.title,  index: this.last
        };

        /**
         * Page container
         *
         * @type {Element}
         */
        this.container = document.querySelector( container );

        /**
         * History path mode (`hash` or `path`)
         *
         * @type {string}
         */
        this.mode = mode || 'hash';

        history.replaceState(this.lastState, document.title, '');

        window.addEventListener('popstate',  async event => {

            if ( event.state )  await this.pop( event.state );
        });
    }

    /**
     * @protected
     *
     * @param {string}       event      - Name of a Custom event
     * @param {boolean}      cancelable - Whether this event can be canceled
     * @param {HistoryState} from       - Meta of leaving page
     * @param {HistoryState} to         - Meta of entering page
     *
     * @return {boolean} Whether `event.preventDefault()` invoked
     */
    dispatch(event, cancelable, from, to) {

        return  this.container.dispatchEvent(new CustomEvent(event, {
            bubbles:     true,
            cancelable:  cancelable,
            detail:      {from, to}
        }));
    }

    /**
     * @protected
     *
     * @return {PageStack}
     */
    cache() {

        this[this.last] = this[this.last] || document.createDocumentFragment();

        this[this.last].append(... this.container.childNodes);

        return this;
    }

    /**
     * @protected
     *
     * @param {string} tag     - Tag name of a Page component
     * @param {string} path    - Route path
     * @param {string} [title]
     */
    record(tag, path, title) {

        title = title || document.title;

        this.lastState = {tag,  path,  title,  index: this.last = this.length++};

        history.pushState(
            this.lastState,  title,  ((this.mode === 'hash') ? '#' : '') + path
        );

        document.title = title;
    }

    /**
     * @param {string} tag     - Tag name of a Page component
     * @param {string} path    - Route path
     * @param {string} [title]
     *
     * @emits {PageChangeEvent}
     * @emits {PageChangedEvent}
     */
    async push(tag, path, title) {

        if ( tag.includes('-') )  await CellLoader.load( tag );

        const previous = history.state,
            next = {tag: document.createElement( tag ),  path,  title};

        previous.tag = this.container.children[0] || '';

        if (! this.dispatch('pagechange', true, previous, next))  return;

        this.cache();

        this.container.append( next.tag );

        this.record(tag, path, title);

        this.dispatch('pagechanged', false, previous, next);
    }

    /**
     * @protected
     *
     * @param {HistoryState} state - `state` property of {@link PopStateEvent}
     */
    async pop(state) {

        this.length = Math.max(this.length,  state.index + 1);

        this.lastState.tag = this.container.children[0] || '';

        var tag = this.cache()[this.last = state.index];

        if (! tag) {

            tag = state.tag;

            if ( tag.includes('-'))  await CellLoader.load( tag );

            tag = document.createElement( tag );
        }

        this.container.append( tag );

        state.tag = this.container.children[0];

        this.dispatch(
            'pagechanged',  false,  this.lastState,  this.lastState = state
        );
    }
}

/**
 * @typedef {Object} HistoryState
 *
 * @property {string|Element} tag   - Page component
 * @property {string}         path  - Route path
 * @property {string}         title
 * @property {number}         index - Stack index of a page
 */

/**
 * Before changing a page
 *
 * @typedef {CustomEvent} PageChangeEvent
 *
 * @property {boolean}       bubbles     - `true`
 * @property {boolean}       cancelable  - `true`
 * @property {Object}        detail
 * @property {HistoryState}  detail.from - Leaving page
 * @property {HistoryState}  detail.to   - Entering page
 */

/**
 * After changing a page
 *
 * @typedef {CustomEvent} PageChangedEvent
 *
 * @property {boolean}       bubbles     - `true`
 * @property {boolean}       cancelable  - `false`
 * @property {Object}        detail
 * @property {HistoryState}  detail.from - Leaving page
 * @property {HistoryState}  detail.to   - Entering page
 */

/**
 * Route map
 */
export default  class RouteMap {

    constructor() {
        /**
         * @protected
         *
         * @type {Map}
         */
        this.map = new Map();
    }

    /**
     * @param {string|RegExp}  route   - **Plain path**, **Path with colon parameters** or
     *                                   **Regular expression**
     * @param {*|RouteHandler} handler
     *
     * @return {RouteMap} This route map
     */
    set(route, handler) {

        const pattern = { route };

        if (route instanceof RegExp)
            pattern.route = route + '',  pattern.pattern = route;
        else {
            if (/\/:[^/]+/.test( route )) {

                pattern.parameter = [ ];

                route = route.replace(
                    /\/:([^/]+)/g,
                    (_, name)  =>  pattern.parameter.push( name ) && '/([^/]+)'
                );
            }

            pattern.pattern = new RegExp(`^${route}`);
        }

        this.map.set(pattern, handler);

        return this;
    }

    /**
     * @param {string} route
     *
     * @return {RouteMap} This route map
     */
    delete(route) {

        for (let item of this.map.keys())
            if (item.route === route)  this.map.delete( item );

        return this;
    }

    /**
     * @param {string} path   - Route path
     * @param {...*}   [data] - Extra data
     *
     * @return {*}
     */
    trigger(path, ...data) {

        for (let route of this.map.entries()) {

            let match = path.match( route[0].pattern );

            if (! match)  continue;

            if (! (route[1] instanceof Function))  return route[1];

            let parameter = { }, index = 1;

            if ( route[0].parameter )
                for (let key of route[0].parameter)
                    parameter[ key ] = match[ index++ ];
            else
                parameter = match.slice(1);

            return  route[1](parameter, ...data);
        }
    }
}

/**
 * Route handler
 *
 * @typedef {function(parameter: Object, data: ...*)} RouteHandler
 */

